mapper.select:
SELECT 
FROM "data_dataset"
WHERE status = true

     AND dataset_id = #{datasetId}
     AND dataset_name = #{datasetName}
     AND datasource_id = #{datasourceId}
     AND sort_sn = #{sortSn}
     AND category_factor_code = #{categoryFactorCode}
     AND is_sdx = #{isSdx}
     AND map_services = #{mapServices}
     AND description = #{description}
     AND status = #{status}
     AND created_time = #{createdTime}
     AND created_by = #{createdBy}
     AND updated_time = #{updatedTime}
     AND updated_by = #{updatedBy}
     AND ${equalFieldName}

             = #{equalFieldValue} 
              is null 


     AND ${searchFieldName} LIKE CONCAT('%', #{searchFieldValue}, '%') 

order by ${orderFieldName}  ${orderMethod}

mapper.update:
UPDATE data_dataset SET status = false
WHERE dataset_id IN

    #{id}
