mapper.update:
UPDATE "auth_user_parttime"
SET status = false
WHERE status = true and user_id = #{userId}

mapper.select:
SELECT 
FROM "auth_user_parttime"
WHERE status = true
and ${searchFieldName} = #{searchFieldValue} 
LIMIT #{pageSize}
OFFSET #{offset}

SELECT 
FROM "auth_user_parttime"  A, "auth_department" B,"auth_post" C
where A.department_id = B.department_id and  A.post_id = C.post_id and A.status = true and B.status = true and C.status = true
and B.department_name like CONCAT('%', #{departmentName}, '%') 
and A.${searchFieldName} = #{searchFieldValue} 
order by A.${orderFieldName} ${orderMethod}
LIMIT #{pageSize}
OFFSET #{offset}

SELECT count(1)
FROM "auth_user_parttime"  A, "auth_department" B,"auth_post" C
where A.department_id = B.department_id and  A.post_id = C.post_id and A.status = true and B.status = true and C.status = true
and B.department_name like CONCAT('%', #{departmentName}, '%') 
and A.${searchFieldName} = #{searchFieldValue} 
order by A.${orderFieldName} ${orderMethod}

        SELECT 
        FROM "auth_user_parttime"
        where status = true
		and ${searchFieldName} = #{searchFieldValue} 
		order by ${orderFieldName} ${orderMethod} 
        LIMIT #{pageSize}
        OFFSET #{offset}

SELECT 
FROM "auth_user_parttime"
WHERE status = true and post_id = #{postId} and user_id = #{userId}

mapper.update:
UPDATE "auth_user_parttime"
SET status = false
WHERE status = true and user_parttime_id = #{userParttimeId}

UPDATE "auth_user_parttime"
user_id = #{userId}, department_id = #{departmentId}, post_id = #{postId}, post_type = #{postType}, expiration_time = #{expirationTime}, status = #{status}, sort_sn = #{sortSn}, updated_time = now(), updated_by = #{updatedBy}, tenant_id = #{tenantId},
WHERE user_parttime_id = #{userParttimeId}
